    },
    ...
}


...
export default class CameraModal extends Component {
    ...
    static propTypes = {
        ...
        setPicture: PropTypes.func,
    }
    ...
    takePicture() {
        ...
        .then(blob => {
            this.props.setPicture(blob)
        });
