transfer.test.js

it('should transfer 100 euros', function(done) {
  // Setup data
  var data = getDefaultData()
  data.BankAccount = [
    {id: 1, personId: 1, bankId: 1, amount: 100},
    {id: 1, personId: 2, bankId: 1, amount: 0}
